mapper.select:
select likedcheck 
from liked 
where music_no=#{music_no} 
  and user_no=#{user_no}

mapper.insert:
INSERT INTO liked (
	user_no, music_no
) VALUES (
	${user_no},
	${music_no},
)

mapper.update:
UPDATE liked SET
	likedcheck = #{checked} 
WHERE
	user_no= #{user_no} and music_no = #{music_no}
